pragma solidity 0.8.9;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "../library/StakingLibrary.sol";
import "../interfaces/IUniswapV2Router02.sol";

contract PMAirDropper is Ownable {
    /**
    * Network: Goerli
    * Aggregator: ETH/USD
    * Address: 0xD4a33860578De61DBAbDc8BFdb98FD742fA7028e
    */


    /**
    * Network: BNB Chain Mainnet
    * Aggregator: BNB/USD
    * Address: 0x0567F2323251f0Aab15c8dFb1967E4e8A7D42aeE
    */

    AggregatorV3Interface internal priceFeed = AggregatorV3Interface(0xD4a33860578De61DBAbDc8BFdb98FD742fA7028e);

    event Received(address, uint);

    address public giveAwayManager;
    GiveAwayPrizeScheme public giveAwayPrizeScheme;

    struct GiveAwayPrizeScheme {
        uint8 firstPrize;
        uint8 remainingPrize;
    }


    constructor(
        address _giveAwayManager, 
        GiveAwayPrizeScheme memory _giveAwayPrizeScheme){
        giveAwayManager = _giveAwayManager;
        giveAwayPrizeScheme = _giveAwayPrizeScheme;
    }

    modifier onlyGiveAwayManager(){
        require(msg.sender == giveAwayManager, "No Authorized");
        _;
    }

    function distributeGiveAwayReward(address firstWinner, address[] memory remainingWinners) public onlyGiveAwayManager {

        uint256 priceOfOneUSD = uint256(getLatestPriceOfOneUSD());

        uint256 totalPrize = priceOfOneUSD * 
            (giveAwayPrizeScheme.firstPrize + 
            remainingWinners.length * giveAwayPrizeScheme.remainingPrize);
        
        uint256 totalBalance = address(this).balance;

        require(totalBalance >=  totalPrize, "Not Enough balance to distribute Reward");

        payable(firstWinner).transfer(priceOfOneUSD * giveAwayPrizeScheme.firstPrize);

        for(uint8 i=0; i < remainingWinners.length; i++){
            payable(remainingWinners[i]).transfer(priceOfOneUSD * giveAwayPrizeScheme.remainingPrize);
        }

    }


    function getLatestPriceOfOneUSD() public view returns (int) {

        // (, int price,,,) = priceFeed.latestRoundData();
        
        // this is the price of 1 Eth in USDs  => 1 ETh = price USDs
        // Find price of 1 USD => 1 USD = 1/price ETH

        // int ONE_ETH = 1 ether;
        // return (ONE_ETH * 10**8)/price;

        // int random = 756881949122395;
        return int(756881949122395);

    }

    function emergencyWithdraw() public onlyOwner {
        uint256 totalBalance = address(this).balance;
        require(totalBalance > 0, "No balance avaialble for withdraw");
        payable(owner()).transfer(totalBalance);
    }

    receive() external payable {
        emit Received(msg.sender, msg.value);
    }

}
