pragma solidity 0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "../interfaces/IStakingPoolFactory.sol";
import "../interfaces/IStakingPool.sol";
import "../library/StakingLibrary.sol";

// import "hardhat/console.sol";
// import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";


contract CreatorContract is ERC721Holder, Ownable {
    
    EnumerableSet.AddressSet private myPoolAddresses;

    uint256 public counter;
    address public stakingPoolFactory;

    constructor(address _owner, address _stakingPoolFactory){
        transferOwnership(_owner);
        stakingPoolFactory = _stakingPoolFactory;
    }

    function onERC721Received(address, address, uint256, bytes memory data) public virtual override returns (bytes4) {
        // onERC721Received(address operator, address from, uint256 tokenId, bytes data) → bytes4

        address tokenAddress;
        assembly {
            tokenAddress := mload(add(data,20))
        } 
        addPoolAddress(tokenAddress);
        return this.onERC721Received.selector;
    }

    error INVALID_POOLID();
    error INVALID_TOKEN_ID();
    error NOT_AUTHER();
    error ALLREADY_UNSTAKED();

    function unstakeTokens(uint256 _tokenId, uint _poolId) public onlyOwner {
        address stakingPool = IStakingPoolFactory(stakingPoolFactory).getPoolByID(_poolId);
        if(stakingPool == address(0)){
            revert INVALID_POOLID();
        }

        StakingLibrary.TokenData memory token = IStakingPool(stakingPool).getTokenData(_tokenId);
        if(token.owner == address(0)){
            revert INVALID_TOKEN_ID();
        }

        if(!token.isUnskated){
            IERC20(token.tokenAddress).transfer(msg.sender, token.tokenStaked);
            IStakingPool(stakingPool).unstakeTokens(_tokenId);
            // if(IStakingPool(stakingPool).balanceOf(msg.sender) === 0){
                // removePoolId(_poolId);
            // }
        }
        else {
            revert ALLREADY_UNSTAKED();
        }
    }

    /* Functions to deal with Pools */

    function addPoolAddress(address _poolAddress) public {
        EnumerableSet.add(myPoolAddresses, _poolAddress);
    }

    function removePoolAddress(address _poolAddress) public {
        EnumerableSet.remove(myPoolAddresses, _poolAddress);
    }

    function getMyPoolAddresses() public view returns(address[] memory) {
        return EnumerableSet.values(myPoolAddresses);
    }

}","keccak256":"0x49632a2068d924352383564e4e8eff912bd21bb0d72c8cdbbdc6791d92fe643d","license":"MIT"},"contracts/interfaces/IStakingPool.sol":{"content":"// SPDX-License-Identifier: MIT
